import{_ as a,c as d,I as l,j as t,a as s,aS as n,o as i,D as o}from"./chunks/framework.D9NNFhGA.js";const f="/assets/%E4%B8%AA%E4%BA%BA%E6%89%80%E5%BE%97%E7%A8%8E.Dpzdye7B.jpg",k=JSON.parse('{"title":"个人所得税","description":"","frontmatter":{},"headers":[],"relativePath":"note/knowledge/01-个人所得税.md","filePath":"note/knowledge/01-个人所得税.md","lastUpdated":1724773331000}'),x={name:"note/knowledge/01-个人所得税.md"},g=t("h1",{id:"个人所得税",tabindex:"-1"},[s("个人所得税 "),t("a",{class:"header-anchor",href:"#个人所得税","aria-label":'Permalink to "个人所得税"'},"​")],-1),r=n('<h2 id="个人所得税-1" tabindex="-1">个人所得税 <a class="header-anchor" href="#个人所得税-1" aria-label="Permalink to &quot;个人所得税&quot;">​</a></h2><p>实发工资 = 税前工资 - 个人所得税 - 社保/公积金</p><p>全年累计应纳税额 = sum(本年税前工资) - n x 5000元(最低免税金额) - 本年累计专项扣除</p><p>当月个人所得税 = （全年累计应纳税额 * 税率 - 速算扣除数） - 已申报的个税</p><ul><li>减5000元是规定死的，因为每月工资不超过5000的话不需要扣个人所得税</li><li>n 是当前月，一月份n=1 3月份就是n=3</li><li>本年累计专项扣除 就是 五险一金</li><li>税率这个是浮动变化的。要根据你全年累计应纳税额来计算</li><li>速算扣除数 是根据税率浮动的，具体看下图表</li></ul><p>看上去上面的公式有点麻烦，其实我举个例子大家就明白了。</p><p>假如小明的税前工资为2w,每月扣除的五险一金为1000元。</p><table tabindex="0"><thead><tr><th style="text-align:left;">月份</th><th style="text-align:left;">本年税前累计工资</th><th style="text-align:left;">本年累计最低免税金额</th><th style="text-align:left;">本年累计专项扣除（五险一金）</th><th style="text-align:left;">全年累计应纳税额</th><th style="text-align:left;">当月个税税率</th><th style="text-align:left;">速算扣除数</th><th style="text-align:left;">本年累计个人所得税</th><th style="text-align:left;">当月扣除的个税</th></tr></thead><tbody><tr><td style="text-align:left;">2024-01</td><td style="text-align:left;">20000</td><td style="text-align:left;">5000</td><td style="text-align:left;">1000</td><td style="text-align:left;">14000</td><td style="text-align:left;">3%</td><td style="text-align:left;">0</td><td style="text-align:left;">420</td><td style="text-align:left;">420</td></tr><tr><td style="text-align:left;">2024-02</td><td style="text-align:left;">40000</td><td style="text-align:left;">10000</td><td style="text-align:left;">2000</td><td style="text-align:left;">28000</td><td style="text-align:left;">3%</td><td style="text-align:left;">0</td><td style="text-align:left;">840</td><td style="text-align:left;">420</td></tr><tr><td style="text-align:left;">2024-03</td><td style="text-align:left;">60000</td><td style="text-align:left;">15000</td><td style="text-align:left;">3000</td><td style="text-align:left;">42000</td><td style="text-align:left;">10%</td><td style="text-align:left;">2520</td><td style="text-align:left;">1680</td><td style="text-align:left;">840</td></tr><tr><td style="text-align:left;">2024-04</td><td style="text-align:left;">80000</td><td style="text-align:left;">20000</td><td style="text-align:left;">4000</td><td style="text-align:left;">56000</td><td style="text-align:left;">10%</td><td style="text-align:left;">2520</td><td style="text-align:left;">3080</td><td style="text-align:left;">1400</td></tr></tbody></table><div class="tip custom-block github-alert"><p class="custom-block-title">这就是为什么有时候发工资的时候当月的比上个月少发好几百的原因，都是因为税率引起的。</p><p></p></div>',9),y=t("p",null,[t("img",{src:f,alt:""})],-1);function _(c,h,p,m,u,b){const e=o("AdUnitForInArticle");return i(),d("div",null,[g,l(e,{"data-ad-slot":4594918104}),r,l(e,{"data-ad-slot":7338978431}),y,l(e,{"data-ad-slot":4594918104})])}const E=a(x,[["render",_]]);export{k as __pageData,E as default};
