import{_ as s,c as a,m as e,b as t,J as i,f as n,aS as r,E as o,a as d}from"./chunks/framework.CFpLsJMv.js";const p="/assets/github.Cd3R5G0q.png",D=JSON.parse('{"title":"jsdelivr 高速且免费的 CDN 服务","description":"","frontmatter":{},"headers":[],"relativePath":"note/server/cdn/jsdelivr.md","filePath":"note/server/cdn/jsdelivr.md","lastUpdated":1726236941000}'),c={name:"note/server/cdn/jsdelivr.md"},h=r('<h1 id="jsdelivr-高速且免费的-cdn-服务" tabindex="-1">jsdelivr 高速且免费的 CDN 服务 <a class="header-anchor" href="#jsdelivr-高速且免费的-cdn-服务" aria-label="Permalink to &quot;jsdelivr 高速且免费的 CDN 服务&quot;">​</a></h1><p>官网：<a href="https://www.jsdelivr.com" target="_blank" rel="noreferrer">https://www.jsdelivr.com</a></p><h2 id="什么是jsdelivr" tabindex="-1">什么是jsdelivr <a class="header-anchor" href="#什么是jsdelivr" aria-label="Permalink to &quot;什么是jsdelivr&quot;">​</a></h2><p>jsDelivr 是免费、高速且稳定的 CDN 服务，提供了 NPM、Github、WordPress Plugin 和其他自定义网站的资源镜像。</p><p>简而言之，就是可以帮助你将一些网站的链接转换成 jsDelivr 自己的链接，而且是CDN加速的，比如我们在线引入的npm资源或者github地址，国内访问 很慢，我们就可以转成jsDelivr的镜像地址。也可以当做图床使用。</p><h2 id="如何转换" tabindex="-1">如何转换 <a class="header-anchor" href="#如何转换" aria-label="Permalink to &quot;如何转换&quot;">​</a></h2>',6),g=r('<ul><li>npm：https://&lt;jsDelivr加速域名&gt;/npm/&lt;项目&gt;@&lt;版本&gt;/&lt;资源路径&gt;</li><li>ESM: <a href="https://esm.run/" target="_blank" rel="noreferrer">https://esm.run/</a>&lt;项目&gt;@版本/&lt;资源路径&gt;</li><li>GitHub: https://&lt;jsDelivr加速域名&gt;/gh/&lt;用户&gt;/&lt;项目&gt;@&lt;版本&gt;/&lt;资源路径&gt;</li><li>WordPress: https://&lt;jsDelivr加速域名&gt;/wp/plugins/&lt;项目&gt;/tags/&lt;版本&gt;/&lt;资源路径&gt;</li></ul><p>拿github举例子 : <a href="https://www.jsdelivr.com/github" target="_blank" rel="noreferrer">https://www.jsdelivr.com/github</a></p><blockquote><p>注意：下面的地址 是 仓库资源路径的地址。 <img src="'+p+'" alt="github"></p></blockquote><p><a href="https://github.com/coder-lzh/tool/blob/master/docs/public/images/logo.png" target="_blank" rel="noreferrer">https://github.com/coder-lzh/tool/blob/master/docs/public/images/logo.png</a></p><p>转换成 （可直接访问）</p><p><a href="https://cdn.jsdelivr.net/gh/coder-lzh/tool@master/docs/public/images/logo.png" target="_blank" rel="noreferrer">https://cdn.jsdelivr.net/gh/coder-lzh/tool@master/docs/public/images/logo.png</a></p><h2 id="解决cdn-jsdelivr-net无法访问" tabindex="-1">解决cdn-jsdelivr-net无法访问 <a class="header-anchor" href="#解决cdn-jsdelivr-net无法访问" aria-label="Permalink to &quot;解决cdn-jsdelivr-net无法访问&quot;">​</a></h2><p>目前已知打不开的域名：</p><ul><li>cdn.jsdelivr.net （国内访问不了）</li></ul><p>但其实还有其他域名可以替代，</p><p>比如：</p><ul><li>gcore.jsdelivr.net (可用)</li><li>fastly.jsdelivr.net (不可用)</li><li>testingcf.jsdelivr.net (可用)</li><li>jsd.cdn.zzko.cn (可用)</li></ul><p>比如：</p><p><a href="https://cdn.jsdelivr.net/gh/coder-lzh/picture@main/1.png" target="_blank" rel="noreferrer">https://cdn.jsdelivr.net/gh/coder-lzh/picture@main/1.png</a> 不可用</p><p>改成下面的就可以访问</p><p><a href="https://gcore.jsdelivr.net/gh/coder-lzh/picture@main/1.png" target="_blank" rel="noreferrer">https://gcore.jsdelivr.net/gh/coder-lzh/picture@main/1.png</a></p>',16);function _(u,v,m,j,b,f){const l=o("font");return d(),a("div",null,[h,e("p",null,[t("jsDelivr加速域名 一般为 "),e("strong",null,[i(l,{color:"red"},{default:n(()=>[t("cdn.jsdelivr.net")]),_:1})])]),g])}const w=s(c,[["render",_]]);export{D as __pageData,w as default};
